import React, {useState} from 'react';
import api from './services/api';
import './App.css';

import logo from './assets/logo.svg';


function App() {
  const [email , setEmail] = useState('');


async function handleSubmit(event){
  event.preventDefault();

  //botar email aqui
  const response =  await api.post('./sessions', { email : email });

  const { _id } = response.data;

  localStorage.setItem('user', _id);  // salvar usuario no banco de dados

}
  return (
    <div className="container">
        <img src={logo} alt="AirCnC"/>     

        <div className="content">
          <p>
            Ofere√ßa <strong>spots</strong> para programadores e encontre talentos para sua empresa! Zum Zaravalho
          </p>

          <form onSubmit = {handleSubmit}>
            <label htmlFor="email">E-MAIL *</label>
            <input 
                type="email" 
                id="email" 
                placeholder = "Seu melhor e-mail" 
                value = {email}
                onChange={event => setEmail(event.target.value)}
            />

            <button className="btn" type = "submit">Entrar</button>
          </form>
        </div>
    </div>
  );
}

export default App;
